mapper.insert:
INSERT INTO tbl_reply (
	rno, bno, 
	reply, replyer, 
	gno, sno, depth
)
VALUES (
	seq_reply.nextval, #{ bno }, 
	#{ reply }, #{ replyer },
	#{ gno }, #{ sno }, #{ depth }
)

mapper.select:
SELECT rno, bno, reply, replyer, replydate, updatedate, gno, sno, depth, deleted
FROM tbl_reply
WHERE rno = #{ rno }

mapper.update:

UPDATE tbl_reply
SET sno = sno + 1
WHERE gno = #{ gno } AND sno > #{ sno }

UPDATE tbl_reply
SET deleted = '1'
WHERE rno = #{ rno }

UPDATE tbl_reply
SET reply = #{ reply }
WHERE rno = #{ rno }
